<?phpnamespace common\models\game;use backend\models\BaseModel;use Yii;/** * This is the model class for table "fivepk_seoid_diamond". * * @property integer $id * @property string $seoid * @property integer $diamond * @property integer $status */class SeoidDiamond extends BaseModel{    /**     * @inheritdoc     */    public static function tableName()    {        return 'fivepk_seoid_diamond';    }    /**     * @return \yii\db\Connection the database connection used by this AR class.     */    public static function getDb()    {        return Yii::$app->get('game_db');    }    /**     * @inheritdoc     */    public function rules()    {        return [            [['diamond', 'status'], 'integer'],            [['seoid'], 'string', 'max' => 100],            [['seoid'], 'unique']        ];    }    /**     * @inheritdoc     */    public function attributeLabels()    {        return [            'id' => 'ID',            'seoid' => 'Seoid',            'diamond' => 'Diamond',            'status' => '状态 0-正常 1-封禁',        ];    }    /**     *  根据seoid查找所有数据     */    public function findBySeoids($PopCodes)    {        return self::find()->where(['in', 'seoid', $PopCodes])->asArray()->all();    }    /**     *  根据seoid查找所有数据     */    public function findBySeoid($PopCodes)    {        return self::find()->where('seoid = :seoid',array(':seoid'=>$PopCodes))->asArray()->one();    }    public function findBase($id)    {        return self::find()->where('id = :id',array(':id'=>$id))->asArray()->all();    }    public function delByPopCode($popCode)    {        return self::deleteAll("seoid=:seoid",[':seoid'=>$popCode]);    }}